import React, { useCallback, useEffect, useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    console.log("useEffect called");
    fetchMovieHandler();
  }, [fetchMovieHandler]);
  // It is better to add all dependencies you use in useEffect
  // fetchMovieHandler add to the dependencies - because if that
  // function change, useEffect should be re-executed.
  // fetchMovieHandler could change if we use external state in that function

  // PROBLEM - Functions are objects, so fetchMovieHandler will change when this component re-render
  // So useEffect works as an infinite loop
  // Solution 1 - [] - remove fetchMovieHandler from dependencies - but
  // if fetchMovieHandler use some external state, then it will be an error

  // Better solution is useCallback hook

  const fetchMovieHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);

    try {
      const response = await fetch("https://localhost:7284/movie");
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();

      const transformedMovies = data.map((movieData) => {
        return {
          id: movieData.episode_id,
          title: movieData.title,
          openingText: movieData.opening_Crawl,
          releaseData: movieData.release_Date,
        };
      });
      setMovies(transformedMovies);
    } catch (error) {
      setError(error.message);
    } finally {
      setIsLoading(false);
    }
  }, []);

  let content = <p>Found no movies!</p>;

  if (movies.length === 0) {
    content = <p>Found no movies!</p>;
  }

  if (movies.length > 0) {
    content = <MoviesList movies={movies} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = (
      <div className="spinner-border text-primary" role="status">
        <span className="visually-hidden">Loading...</span>
      </div>
    );
  }

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMovieHandler}>Fetch Movies</button>
      </section>
      <section>{content}</section>
    </React.Fragment>
  );
}

export default App;
